/**
 * UBnext theme functions - js
 */

// Define constants
var 
  scroller,
  contentOffset,
  windowwidth,
  windowheight,
  scroll,
  nav,
  particlesColor = '#fff';

// Init WOW js
new WOW().init();

// init jQuery noConflict - Just to be sure
var $jquery = jQuery.noConflict();

/**
 * UBnext theme functions - setup listeners
 */

// Window resize
$jquery(window).on('resize', function(){
  setBlockheight();
  initBanner(true);
  contentOffset = $jquery('div#main-content').offset().top;
});

// Window load
$jquery(window).on('load', function(){
    initBanner(false);
    contentOffset = $jquery('div#main-content').offset().top;
    $jquery('body').focus();
});

// Window scroll
$jquery(window).on('scroll', function(){
  scroll = $jquery(window).scrollTop();

  // Defined in header.php
  if( hideHeaderOnScroll ){
    if( scroll >= contentOffset ){
      nav.fadeOut();
    }else{
      nav.fadeIn();
    }
  }
});

// Document ready
$jquery( document ).ready( function(){
  globalInit();
  initGallery();
  setBlockheight();
  initGrid();
  initMenu();
});

function globalInit(){
  nav = $jquery('nav#navbar');
  contentOffset = $jquery('div#main-content').offset().top;
  
  $jquery('.add-tooltip').tooltipster({
    side:'bottom',
    content: 'Loading cart...',
    functionBefore: function(instance, helper) {
      var $origin = $jquery(helper.origin);
      // we set a variable so the data is only loaded once via Ajax, not every time the tooltip opens
      if ($origin.data('loaded') !== true) {
          $jquery.get( localize.ajaxurl + '?action=urnext_get_cart_contens', function(data) {

              // call the 'content' method to update the content of our tooltip with the returned data.
              // note: this content update will trigger an update animation (see the updateAnimation option)
              instance.content(data);

              // to remember that the data has been loaded
              $origin.data('loaded', true);
          });
      }
    }
  });

  $jquery('#scroll-down').on('click', function(e){
    e.preventDefault();
    $jquery("html, body").animate({ scrollTop: contentOffset }, 1000);
  });
  $jquery(".textadjust").fitText(
    2,
    { 
      minFontSize: '17px',
      maxFontSize: '20px'
    }
  );
  $jquery(".headadjust").fitText(
    2,
    { 
      minFontSize: '22px',
      maxFontSize: '60px'
    }
  );

}

function setWindowwidth(){
  windowwidth = $jquery(window).width();
}

function setWindowheight(){
  windowheight = $jquery(window).height();
}

function setBlockheight(){
  setWindowwidth();
  setWindowheight();
  $jquery('div.grid div.grid-item').each(function(){
    var gridWidth = $jquery(this).width();
    var gridHeight = $jquery(this).hasClass('full') ? ( gridWidth / 2 ) : gridWidth ;
      $jquery(this).height( gridHeight );
  });
}

function initGallery(){
  $jquery('.slick-gallery').slick({
    centerMode: true,
    
    centerPadding: 0,
    slidesToShow: 3,
    infinite: true,
    dots:true,
    arrows: false,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
        }
      },
      {
        breakpoint: 768,
        settings: {
          slidesToShow: 1,
        }
      }
    ]
  });
}

function initGrid(){
  $jquery('.grid').isotope({
    itemSelector: '.grid-item',
    percentPosition: true,
    layoutMode: 'masonry',
    masonry: {
      // use element for option
      columnWidth: '.sizer'
    }
  });
}

